        public async Task<List<ChartData>> GetChartData(string sName)
        {
            try
            {
                string fromDate = DateAndTime.DateAdd(DateInterval.Month, -6, DateTime.Today).ToString("yyyy-MM-dd");
                string toDate = DateAndTime.DateAdd(DateInterval.Day, -1, DateTime.Today).ToString("yyyy-MM-dd");
                //string websiteUrl = "https://api.polygon.io/v2/aggs/ticker/AAPL/range/1/day/2022-01-01/2022-06-22?adjusted=true&sort=asc&limit=120&apiKey=ZqJe9jQrip7ulI2Pa6fkICLddpneFoqK";
                string websiteUrl = $"https://api.polygon.io/v2/aggs/ticker/{sName}/range/1/day/{fromDate}/{toDate}?adjusted=true&sort=asc&limit=120&apiKey=ZqJe9jQrip7ulI2Pa6fkICLddpneFoqK";
                var httpClient = new HttpClient();
                HttpResponseMessage response = null;
                string content = null;
                List<ChartData> lista = new List<ChartData>();

                response = await httpClient.GetAsync(websiteUrl);
                content = await response.Content.ReadAsStringAsync();

                JObject Search = JObject.Parse(content);
                IList<JToken> results = Search["results"].Children().ToList();
                int counter = 0;

                foreach (JToken result in results)
                {
                    counter++;
                    SomSortOdChartData searchResult = result.ToObject<SomSortOdChartData>();

                    lista.Add(new ChartData
                    {
                        x = DateAndTime.DateAdd(DateInterval.Day, -counter, DateTime.Today),
                        open = searchResult.o,
                        low = searchResult.l,
                        close = searchResult.c,
                        high = searchResult.h,
                        volume = searchResult.v

                    });
                }
                return lista;
            } catch (Exception ex)
            {
                return new List<ChartData>();
            }
        }

        public async Task<TickerDet1> GetTickerInfo(string sName)
        {
            try
            {
                string websiteUrl = "https://api.polygon.io/v3/reference/tickers/" + sName + "?date=" + DateAndTime.DateAdd(DateInterval.Day, -1, DateTime.Today).ToString("yyyy-MM-dd") + "&apiKey=ZqJe9jQrip7ulI2Pa6fkICLddpneFoqK";
                var httpClient = new HttpClient();
                HttpResponseMessage response = null;
                string content = null;
                TickerDet1 tickerDet = new TickerDet1();

                response = await httpClient.GetAsync(websiteUrl);
                content = await response.Content.ReadAsStringAsync();

                JObject Search = JObject.Parse(content);
                tickerDet.name = Search["results"]["name"].ToString();
                tickerDet.sname = sName;
                tickerDet.market = Search["results"]["market"].ToString();
                tickerDet.locale = Search["results"]["locale"].ToString();
                tickerDet.address = Search["results"]["address"]["address1"].ToString();
                tickerDet.city = Search["results"]["address"]["city"].ToString();
                tickerDet.description = Search["results"]["description"].ToString();
                tickerDet.logo_url = Search["results"]["branding"]["logo_url"].ToString();

                return tickerDet;
            }catch (NullReferenceException ex)
            {
                var ele = await _dbContext.WatchedList.Where(e => e.sname == sName).FirstOrDefaultAsync();
                if ((ele is null)) return new TickerDet1 { name="null", market="null"};
                else return new TickerDet1
                {
                    name = ele.name,
                    sname = ele.sname,
                    market = ele.market,
                    locale = ele.locale,
                    address = ele.address,
                    city = ele.city,
                    description = ele.description,
                    logo_url = ele.logo_url,
                    ID = ele.ID
                };
            }
        }

