        public async Task<bool> PostMyListElement(TickerDet1 ticker)
        {
            using (var tran = await _dbContext.Database.BeginTransactionAsync())
            {
                _dbContext.MyList.Add(ticker);

                await _dbContext.SaveChangesAsync();
                await tran.CommitAsync();

                return true;
            }
        }

        public async Task<bool> CheckMyListElement(TickerDet1 ticker)
        {
            var ele = await _dbContext.MyList.Where(e => e.name == ticker.name).FirstOrDefaultAsync();
            if (ele is null) return false;
            return true;
        }

        public async Task<List<TickerDet1>> GetMyList(string ID)
        {
            return await _dbContext.MyList.Where(e => e.ID == ID).ToListAsync();
        }

        public async Task<bool> DeltMyListElement(string sname)
        {
            using (var tran = await _dbContext.Database.BeginTransactionAsync())
            {
                var ele = await _dbContext.MyList.Where(e => e.sname == sname).FirstOrDefaultAsync();
                _dbContext.MyList.Remove(ele);

                await _dbContext.SaveChangesAsync();
                await tran.CommitAsync();

                return true;
            }
        }

        public async Task<bool> PostMyWatchElement(TickerDet2 ticker)
        {
            using (var tran = await _dbContext.Database.BeginTransactionAsync())
            {
                var ele = await _dbContext.WatchedList.Where(e => e.name == ticker.name).FirstOrDefaultAsync();
                if (!(ele is null)) return false;

                _dbContext.WatchedList.Add(ticker);

                await _dbContext.SaveChangesAsync();
                await tran.CommitAsync();

                return true;
            }
        }
    }
